  deleteData(type, id) {
    if (type && id) {
      if (type === "Email") {
        var emailarray = [...this.state.emailTemplatesList]; // make a separate copy of the array

        var index = emailarray.findIndex(obj => {
          return obj._id === id;
        });

        if (index !== -1) {
          emailarray.splice(index, 1);
          this.setState({
            emailTemplatesList: emailarray,
            emailTemplates: {}
          }, () => this.getData());
        }
      } else if (type === "Notification") {
        var notificationarray = [...this.state.notificationTemplatesList]; // make a separate copy of the array

        var notificationindex = notificationarray.findIndex(obj => {
          return obj._id === id;
        });

        if (notificationindex !== -1) {
          notificationarray.splice(notificationindex, 1);
          this.setState({
            notificationTemplatesList: notificationarray,
            notificationTemplates: {}
          });
        }
      } else if (type === "SMS") {
        var smsarray = [...this.state.smsTemplatesList]; // make a separate copy of the array

        var smsindex = smsarray.findIndex(obj => {
          return obj._id === id;
        });

        if (smsindex !== -1) {
          smsarray.splice(smsindex, 1);
          this.setState({
            smsTemplatesList: smsarray,
            smsTemplates: {}
          });
        }
      }
    }
  }

