  }

  componentWillReceiveProps(nextProps, prevProps) {}

  getDesignation() {
    axios.get("/api/designationmaster/get/list").then(response => {
      var designationArray = [];
      response.data.map((data, ind) => {
        designationArray.push({
          id: data._id,
          designation: data.designation
        });
      });
      this.setState({
        designationArray: designationArray
      });
    }).catch(error => {});
  }

  getDepartment() {
    axios.get("/api/departmentmaster/get/list").then(response => {
      var departmentArray = [];
      response.data.map((data, ind) => {
        departmentArray.push({
