          department: data.department
        });
      });
      this.setState({
        departmentArray: departmentArray
      });
    }).catch(error => {});
  }

  getStates(StateCode) {
    axios.get("http://locations2.iassureit.com/api/states/get/list/" + StateCode).then(response => {
      this.setState({
        stateCode: "Select State",
        statesArray: response.data
      });
    }).catch(error => {});
  }

  handleChangeState(stateCode) {
    this.getDistrict(stateCode, 'IN');
  }

  getDistrict(stateCode, countryCode) {
    axios.get("http://locations2.iassureit.com/api/districts/get/list/" + countryCode + "/" + stateCode).then(response => {
      this.setState({
        district: "Select District",
        districtArray: response.data
      });
    }).catch(error => {});
  }

  camelCase(str) {
    return str.toLowerCase().split(' ').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
  }

  getpersons() {
    var getcompanyID = localStorage.getItem("companyID");
    var formvalues = {
      type: this.state.type
    };
    axios.post("/api/personmaster/get/list", formvalues).then(response => {
      var tableData = response.data.filter((data, i) => {
        return data.status !== 'deleted-Active' && data.status !== 'deleted-Inactive';
      });

      if (this.state.roles.indexOf("admin") == -1) {
        this.setState({
          personList: tableData.filter(person => person.companyID == getcompanyID),
          personCount: tableData.length
        });
      } else {
        this.setState({
          personList: tableData,
          personCount: tableData.length
        });
      }

      document.getElementById(tableData[0]._id).classList.add("selectedSupplier");
      this.setState({
        id: tableData[0]._id,
        showDetails: true
      });
    }).catch(error => {});
  }

  deletedDriver() {
    var formvalues = {
      type: this.state.type
    };
    axios.post("/api/personmaster/get/list", formvalues).then(response => {
